/* eslint-disable import/no-anonymous-default-export */
import React from 'react'
import { useSelector } from 'react-redux'

const Notification = () => {
  const notification = useSelector(store => store.notification)
  const style1 = {
    border: 'solid',
    padding: 10,
    borderWidth: 1,
    color: 'red',
    background: 'lightgrey',
    fontSize: 20,
    borderRadius: 5,
    marginBottom: 10
  }

  const style2 = {
    border: 'solid',
    padding: 10,
    borderWidth: 1,
    color: 'red',
    background: 'lightgrey',
    fontSize: 20,
    borderRadius: 5,
    marginBottom: 10
  }

  return (
    <div style={notification.toShow ? (notification.notiype ? style1 : style2 ) : { display: 'none' }}>
      {notification.message}
    </div>
  )
}

export default Notification
