/* eslint-disable no-unused-vars */
import React, { useState, useEffect, useRef } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import BlogForm from './components/BlogForm'
import LoginForm from './components/LoginForm'
import Togglable from './components/Togglable'
import Notification from './components/Notification'
import { useDispatch } from 'react-redux'
import { setSuccessNotification, setErrorNotification } from './reducers/notificationReducer'
import { createBlog, AddLikes, DeleteBlog } from './reducers/blogReducer'
import { initializeBlogs } from './reducers/blogReducer'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(initializeBlogs())
  }, [dispatch])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const handleLogin = async (event) => {
    event.preventDefault()

    try {
      const user = await loginService.login({
        username, password,
      })

      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      dispatch(setErrorNotification('wrong username or password', 5))
    }
  }

  const loginForm = () => {
    return (
      <LoginForm
        username={username}
        password={password}
        handleUsernameChange={({ target }) => setUsername(target.value)}
        handlePasswordChange={({ target }) => setPassword(target.value)}
        handleSubmit={handleLogin}
      />
    )
  }

  const handleLogout = async (event) => {
    window.localStorage.removeItem('loggedBlogappUser')

    blogService.setToken(null)
    setUser(null)
    setUsername('')
    setPassword('')
    dispatch(setSuccessNotification('logged out successfully', 5))
  }

  const blogFormRef = useRef()

  const addBlog = (blogObject) => {
    blogFormRef.current.toggleVisibility()
      dispatch(createBlog(blogObject))
      dispatch(setSuccessNotification(`a new blog '${blogObject.title}' by ${blogObject.author} added`, 5))
      .catch (error => {
        console.log(error)
        dispatch(setErrorNotification(error.response.data.error, 5))
      })
  }

  const creatBlogForm = () => (
    <Togglable buttonLabel='new blog' ref={blogFormRef}>
      <BlogForm createBlog={addBlog} user={user.username}/>
    </Togglable>
  )

  const addLikes = (id) => {
    const blog = blogs.find(blog => blog.id === id)
    dispatch(AddLikes(blog))
  }

  const deleteBlog = (id) => {
    const blog = blogs.find(blog => blog.id === id)
    if (window.confirm(`Remove blog ${blog.title} by ${blog.author}`)){
      if (blog.user.username === user.username || blog.user.username === undefined) {
          dispatch(DeleteBlog(id))
          dispatch(setSuccessNotification(`blog '${blog.title}' by ${blog.author} deleted`, 5))
      } else {
        blogService
          .catch (error => {
            dispatch(setErrorNotification(error.response.data.error, 5))
          })
      }
    }
  }

  const blogList = () => {
    return (
      <div id='showblogs'>
        {blogs.sort((a, b) => parseFloat(b.likes) - parseFloat(a.likes)).map(blog =>
          <Blog key={blog.id} blog={blog} user={user.username}
            addLikes={() => addLikes(blog.id)} deleteBlog={() => deleteBlog(blog.id)}/>
        )}
      </div>
    )
  }

  return (
    <div>
      <Notification />
      {user === null ?
        loginForm() :
        <div>
          <h2>blogs</h2>
          <p>{user.username} logged in<button onClick={handleLogout}>logout</button></p>
          {creatBlogForm()}
          {blogList()}
        </div>
      }
    </div>
  )
}

export default App
